import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, Image, TextInput } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { URLaddress, loggedUser } from './App';
import { FontAwesome } from '@expo/vector-icons';

// Example data for chats and contacts
const exampleChats = [
  { id: 1, name: 'Alice', lastMessage: 'Hey, how are you?' },
  { id: 2, name: 'Jack', lastMessage: 'See you tomorrow!' },
  { id: 3, name: 'Lenny', lastMessage: 'Can you send me that file?' },
];

const exampleContacts = [
  { user_id: 1, first_name: 'Alice', last_name: 'Mc', email: 'alice@gmail.com' },
  { user_id: 2, first_name: 'Jack', last_name: 'Sparrow', email: 'jack@gmail.com' },
  { user_id: 3, first_name: 'Lenny', last_name: 'Stuart', email: 'lenny@gmail.com' },
];

// Define Chats component
function Chats() {
  return (
    <View style={styles.tabContent}>
      {exampleChats.map(chat => (
        <View key={chat.id} style={styles.chatItem}>
          <Text style={styles.chatName}>{chat.name}</Text>
          <Text style={styles.chatLastMessage}>{chat.lastMessage}</Text>
        </View>
      ))}
    </View>
  );
}

// Define Contacts component
function Contacts() {
  return (
    <View style={styles.tabContent}>
      {exampleContacts.map(contact => (
        <View key={contact.user_id} style={styles.contactItem}>
          <Text style={styles.contactName}>{contact.first_name + " " + contact.last_name}</Text>
        </View>
      ))}
      <TouchableOpacity style={styles.newContactButton}>
        <FontAwesome name="user-plus" size={20} color="#FFF" />
        <Text style={styles.newContactButtonText}>New Contact</Text>
      </TouchableOpacity>
    </View>
  );
}

function Profile() {
  const [name, setName] = useState('John');
  const [surname, setSurname] = useState('Doe');
  const [email, setEmail] = useState('john.doe@example.com');
  const [image, setImage] = useState(null);

  // handle input changes
  const handleNameChange = (text) => {
    setName(text);
  }

  const handleSurnameChange = (text) => {
    setSurname(text);
  }

  const handleEmailChange = (text) => {
    setEmail(text);
  }

  const handleImageChange = (event) => {
    const { uri } = event.nativeEvent;
    setImage(uri);
  }

  return (
    <View style={styles.tabContent}>
      <View style={styles.profileImageContainer}>
        {image ? (
          <Image source={{ uri: image }} style={styles.profileImage} />
        ) : (
          <FontAwesome name="user" size={100} color="#555" />
        )}
        <TouchableOpacity style={styles.editImageButton}>
          <Text style={styles.editImageButtonText}>Edit Image</Text>
          <TextInput style={styles.hiddenInput} onChange={handleImageChange} />
        </TouchableOpacity>
      </View>
      
      <View style={styles.profileInfo}>
        <TextInput
          style={styles.input}
          placeholder="Name"
          value={name}
          onChangeText={handleNameChange}
        />
        <TextInput
          style={styles.input}
          placeholder="Surname"
          value={surname}
          onChangeText={handleSurnameChange}
        />
        <TextInput
          style={styles.input}
          placeholder="Email"
          value={email}
          onChangeText={handleEmailChange}
        />
      </View>
    </View>
  );
}


export default function Home({navigation}) {
  
  const [activeTab, setActiveTab] = useState('Chats');

  return (
    <View style={styles.container}>
      {/* Nav bar */}
      <View style={styles.navBar}>
      <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Profile' && styles.activeTabButton]}
          onPress={() => setActiveTab('Profile')}
        >
          {activeTab === 'Profile' ? (
      <Text style={[styles.tabButtonText, styles.activeTabButtonText]}>Profile</Text>
    ) : (
      <FontAwesome name="user-o" size={18} color="#fff" />
    )}
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Chats' && styles.activeTabButton]}
          onPress={() => setActiveTab('Chats')}
        >
          {activeTab === 'Chats' ? (
      <Text style={[styles.tabButtonText, styles.activeTabButtonText]}>Chats</Text>
    ) : (
      <FontAwesome name="comments" size={18} color="#fff" />
    )}
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Contacts' && styles.activeTabButton]}
          onPress={() => setActiveTab('Contacts')}
        >
          {activeTab === 'Contacts' ? (
      <Text style={[styles.tabButtonText, styles.activeTabButtonText]}>Contacts</Text>
    ) : (
      <FontAwesome name="address-book-o" size={18} color="#fff" />
    )}
        </TouchableOpacity>
        <TouchableOpacity onPress={() => navigation.navigate('Chat')}>
        <Text>Go to Chat screen</Text>
      </TouchableOpacity>
      </View>
  
      {/* Tab content */}
      {activeTab === 'Chats' ? <Chats /> : activeTab === 'Contacts' ? <Contacts /> : <Profile />}
    </View>
  );
  
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  navBar: {
    flexDirection: 'row',
    backgroundColor: '#007aff',
    height: 60,
  },
  tabButton: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  tabButtonContent: {
    paddingHorizontal: 10,
  },
  tabButtonText: {
    color: '#fff',
    fontSize: 18,
  },
  activeTabButton: {
    flex: 3,
    borderBottomWidth: 2,
    borderBottomColor: '#fff',
  },
  activeTabButtonText: {
    fontWeight: 'bold',
  },
  tabContent: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'flex-start',
  }, 
  input: {
    width: '100%',
    padding: 10,
    marginBottom: 10,
    backgroundColor: '#f0f0f0',
    borderWidth: 1,
    borderColor: '#ccc',
    borderRadius: 5,
    },
    imageContainer: {
    width: '100%',
    alignItems: 'center',
    marginBottom: 20,
    },
    profileImage: {
    width: 150,
    height: 150,
    borderRadius: 75,
    },
    changeImageButton: {
    backgroundColor: '#007aff',
    padding: 10,
    borderRadius: 5,
    marginTop: 10,
    },
    changeImageButtonText: {
    color: '#fff',
    textAlign: 'center',
    },
    form: {
    width: '100%',
    },
    label: {
    fontWeight: 'bold',
    marginBottom: 5,
    },
    submitButton: {
    backgroundColor: '#007aff',
    padding: 10,
    borderRadius: 5,
    marginTop: 20,
    },
    submitButtonText: {
    color: '#fff',
    textAlign: 'center',
    },
    profileName: {
    fontWeight: 'bold',
    fontSize: 18,
    marginTop: 20,
    },
    profileEmail: {
    color: '#777',
    fontSize: 14,
    marginTop: 5,
    },
    chatItem: {
      width: '100%',
      padding: 20,
      backgroundColor: '#f0f0f0',
      paddingTop: 20,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    chatName: {
      fontWeight: 'bold',
      fontSize: 16,
      marginBottom: 5,
    },
    chatLastMessage: {
      color: '#777',
      fontSize: 14,
    },
    contactItem: {
      width: '100%',
      padding: 20,
      backgroundColor: '#f0f0f0',
      paddingTop: 20,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    contactName: {
      fontWeight: 'bold',
      fontSize: 16,
    },
    newContactButton: {
      position: 'absolute',
      bottom: 16,
      right: 16,
      width: 56,
      height: 56,
      backgroundColor: '#3366FF',
      borderRadius: 28,
      alignItems: 'center',
      justifyContent: 'center',
      shadowColor: '#000',
      shadowOffset: {
        width: 0,
        height: 3,
      },
      shadowOpacity: 0.29,
      shadowRadius: 4.65,
  
      elevation: 7,
    },
    newContactButtonText: {
      color: '#fff',
      fontSize: 32,
      lineHeight: 32,
    },
  });
