import React, { useState } from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';

// Define the Chats component
function Chats() {
  return (
    <View style={styles.content}>
      <Text>Chats</Text>
      <Text>Example chat 1</Text>
      <Text>Example chat 2</Text>
      <Text>Example chat 3</Text>
    </View>
  );
}

// Define the Contacts component
function Contacts() {
  return (
    <View style={styles.content}>
      <Text>Contacts</Text>
      <Text>Example contact 1</Text>
      <Text>Example contact 2</Text>
      <Text>Example contact 3</Text>
    </View>
  );
}

export default function Home() {
  const [activeTab, setActiveTab] = useState('Chats');

  const handleTabPress = (tab) => {
    setActiveTab(tab);
  };

  return (
    <View style={styles.container}>
      <View style={styles.navBar}>
        <TouchableOpacity style={activeTab === 'Chats' ? styles.activeTab : styles.tab} onPress={() => handleTabPress('Chats')}>
          <Text style={styles.tabText}>Chats</Text>
        </TouchableOpacity>
        <TouchableOpacity style={activeTab === 'Contacts' ? styles.activeTab : styles.tab} onPress={() => handleTabPress('Contacts')}>
          <Text style={styles.tabText}>Contacts</Text>
        </TouchableOpacity>
      </View>
      {activeTab === 'Chats' ? <Chats /> : <Contacts />}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
  navBar: {
    flexDirection: 'row',
    backgroundColor: '#007aff',
    height: 50,
    alignSelf: 'stretch',
    justifyContent: 'space-around',
    alignItems: 'center',
  },
  tab: {
    flex: 1,
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
  },
  activeTab: {
    flex: 1,
    height: '100%',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#fff',
    borderTopLeftRadius: 10,
    borderTopRightRadius: 10,
  },
  tabText: {
    color: '#fff',
    fontSize: 18,
  },
  content: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
});
