import React, { useState, useRef, useEffect } from 'react';
import {
  SafeAreaView,
  StyleSheet,
  Text,
  TextInput,
  View,
  ScrollView,
  TouchableOpacity,
  KeyboardAvoidingView,
  Platform
} from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { getChatById, postMessage } from './ChatRequests';
import { URLaddress, loggedUser } from './App';
import { FontAwesome } from '@expo/vector-icons';

const initialMessages = [
  { text: 'Welcome to The Rod Stewart Appreciation Society!', sender: 'bot' },
  { text: 'Please introduce yourself and tell us your favorite Rod Stewart song.', sender: 'bot' },
  { text: 'Hey', sender: 'monkey' },
  { text: 'Hi', sender: 'user' },
  { text: 'dont talk', sender: 'bot' },
  { text: 'nice one mate', sender: 'user' },
];

export default function Chat ({ route }) {
  const { chatId } = route.params;
  const [chat, setChat] = useState({ messages: [] });
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const [showMembers, setShowMembers] = useState(false);
  const scrollViewRef = useRef();
  const navigation = useNavigation();

  useEffect(() => {
    getChatById(setChat, chatId);
  }, []);

  // Scroll to bottom of the message list whenever a new message is added
  useEffect(() => {
    scrollViewRef.current.scrollToEnd({ animated: true });
  }, [messages]);

  // Function to handle sending a message
  const handleSendMessage = () => {
    if (inputText.trim() === '') return;
    setMessages([...messages, { text: inputText, sender: 'user' }]);
    setInputText('');
    console.log("Message sent:", inputText);
    postMessage(chatId, inputText);
    };

    // Function to handle showing the list of members
  const handleShowMembers = () => {
    console.log('Showing members');
  };

  // Function to handle adding a member to the chat
  const handleAddMember = () => {
    console.log('Adding member');
  };
  return (
    <SafeAreaView style={styles.safeAreaContainer}>
      
      
      <View style={styles.headerContainer}>
        <TouchableOpacity style={styles.backButton} onPress={() => navigation.goBack()}>
          <Text style={styles.backButtonText}>{'<'}</Text>
        </TouchableOpacity>
        
        <Text style={styles.headerText}>{chat.name}</Text>
        <View style={styles.placeholderButton}></View>
      </View>
      <KeyboardAvoidingView
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.container}
      >
        <ScrollView
          ref={scrollViewRef}
          onContentSizeChange={() => scrollViewRef.current.scrollToEnd({ animated: true })}
          contentContainerStyle={styles.messageListContainer}
        >
{chat.messages.sort((a, b) => a.timestamp - b.timestamp).map((message, index) => (
  <View
    key={index}
    style={[
      styles.messageContainer,
      message.author.user_id === loggedUser.userId ? styles.userMessageContainer : styles.otherMessageContainer
    ]}
  >
    <Text>
      <Text style={message.author.user_id === loggedUser.userId ? styles.userMessageText : styles.otherMessageText}>
        {message.author.user_id === loggedUser.userId ? '' : `${message.author.first_name}: `}
      </Text>
      <Text style={message.author.user_id === loggedUser.userId ? styles.userMessageText : styles.otherMessageText}>
        {message.message}
      </Text>
      <Text style={styles.messageTime}>
        {new Date(message.timestamp).toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'})}
      </Text>
    </Text>
  </View>
))}



          {chat && chat.messages && chat.messages.length === 0 && (
  <Text style={styles.noMessagesText}>No messages yet.</Text>
)}
        </ScrollView>
        <View style={styles.inputContainer}>
          <TextInput
            value={inputText}
            onChangeText={setInputText}
            placeholder="Type a message"
            style={styles.textInput}
            onSubmitEditing={handleSendMessage}
          />
          <TouchableOpacity style={styles.sendButton} onPress={handleSendMessage}>
            <Text style={styles.sendButtonText}>Send</Text>
          </TouchableOpacity>
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  safeAreaContainer: {
  flex: 1,
  backgroundColor: '#fff'
  },
  container: {
  flex: 1,
  backgroundColor: '#F5F5F5',
  paddingVertical: 10,
  paddingHorizontal: 10
  },
  headerContainer: {
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'space-between',
  backgroundColor: '#3366FF',
  paddingHorizontal: 10,
  paddingVertical: 5,
  marginBottom: 10
  },
  headerTitle: {
  color: '#FFFFFF',
  fontSize: 18,
  fontWeight: 'bold'
  },
  backButton: {
  backgroundColor: '#FFFFFF',
  borderRadius: 10,
  paddingVertical: 5,
  paddingHorizontal: 10
  },
  backButtonText: {
  color: '#3366FF',
  fontWeight: 'bold',
  fontSize: 16
  },
  messageListContainer: {
  flexGrow: 1
  },
  messageContainer: {
  backgroundColor: '#FFFFFF',
  borderRadius: 10,
  maxWidth: '80%',
  marginBottom: 10,
  alignSelf: 'flex-start'
  },
  userMessageContainer: {
  backgroundColor: '#DCF8C6',
  alignSelf: 'flex-end'
  },
  otherMessageContainer: {
  backgroundColor: '#FFFFFF'
  },
  userMessageText: {
  color: '#000000',
  fontSize: 16
  },
  otherMessageText: {
  color: '#000000',
  fontSize: 16
  },
  inputContainer: {
  flexDirection: 'row',
  alignItems: 'center',
  justifyContent: 'space-between',
  backgroundColor: '#FFFFFF',
  paddingHorizontal: 10,
  paddingVertical: 5
  },
  textInput: {
  flex: 1,
  height: 40,
  borderWidth: 1,
  borderColor: '#C8C8C8',
  borderRadius: 10,
  paddingHorizontal: 10,
  marginRight: 10
  },
  sendButton: {
  backgroundColor: '#3366FF',
  borderRadius: 10,
  paddingVertical: 10,
  paddingHorizontal: 20
  },
  sendButtonText: {
  color: '#FFFFFF',
  fontWeight: 'bold'
  },
  noMessagesText: {
  color: '#000000',
  textAlign: 'center'
  }
  });
