import React, { useState, useRef, useEffect } from 'react';
import {
  SafeAreaView,
  StyleSheet,
  Text,
  TextInput,
  View,
  ScrollView,
  TouchableOpacity,
  KeyboardAvoidingView,
  Platform
} from 'react-native';


const initialMessages = [
  { text: 'Welcome to The Rod Stewart Appreciation Society!', sender: 'bot' },
  { text: 'Please introduce yourself and tell us your favorite Rod Stewart song.', sender: 'bot' },
];

export default function Chat () {
  const [messages, setMessages] = useState(initialMessages);
  const [inputText, setInputText] = useState('');
  const scrollViewRef = useRef();

  // Scroll to bottom of the message list whenever a new message is added
  useEffect(() => {
    scrollViewRef.current.scrollToEnd({ animated: true });
  }, [messages]);

  // Function to handle sending a message
  const handleSendMessage = () => {
    if (inputText.trim() === '') return;
    setMessages([...messages, { text: inputText, sender: 'user' }]);
    setInputText('');
  };

  return (
    <SafeAreaView style={styles.safeAreaContainer}>
      <KeyboardAvoidingView
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.container}
      >
        <ScrollView
          ref={scrollViewRef}
          onContentSizeChange={() => scrollViewRef.current.scrollToEnd({ animated: true })}
          contentContainerStyle={styles.messageListContainer}
        >
          {messages.map((message, index) => (
            <View
              key={index}
              style={[
                styles.messageContainer,
                message.sender === 'user' ? styles.userMessageContainer : styles.otherMessageContainer
              ]}
            >
              <Text style={message.sender === 'user' ? styles.userMessageText : styles.otherMessageText}>
                {message.text}
              </Text>
            </View>
          ))}
          {messages.length === 0 && (
            <Text style={styles.noMessagesText}>No messages yet.</Text>
          )}
        </ScrollView>
        <View style={styles.inputContainer}>
          <TextInput
            value={inputText}
            onChangeText={setInputText}
            placeholder="Type a message"
            style={styles.textInput}
            onSubmitEditing={handleSendMessage}
          />
          <TouchableOpacity style={styles.sendButton} onPress={handleSendMessage}>
            <Text style={styles.sendButtonText}>Send</Text>
          </TouchableOpacity>
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
    safeAreaContainer: {
      flex: 1,
      backgroundColor: '#fff'
    },
    container: {
      flex: 1,
      backgroundColor: '#F5F5F5',
      paddingVertical: 10,
      paddingHorizontal: 10
    },
    messageListContainer: {
      flexGrow: 1
    },
    messageContainer: {
      backgroundColor: '#FFFFFF',
      borderRadius: 10,
      maxWidth: '80%',
      marginBottom: 10,
      alignSelf: 'flex-start'
    },
    userMessageContainer: {
      backgroundColor: '#DCF8C6',
      alignSelf: 'flex-end'
    },
    otherMessageContainer: {
      backgroundColor: '#FFFFFF'
    },
    userMessageText: {
      color: '#000000',
      fontSize: 16
    },
    otherMessageText: {
      color: '#000000',
      fontSize: 16
    },
    inputContainer: {
      flexDirection: 'row',
      alignItems: 'center',
      justifyContent: 'space-between',
      backgroundColor: '#FFFFFF',
      paddingHorizontal: 10,
      paddingVertical: 5
    },
    textInput: {
      flex: 1,
      height: 40,
      borderWidth: 1,
      borderColor: '#C8C8C8',
      borderRadius: 10,
      paddingHorizontal: 10,
      marginRight: 10
    },
    sendButton: {
      backgroundColor: '#3366FF',
      borderRadius: 10,
      paddingVertical: 10,
      paddingHorizontal: 20
    },
    sendButtonText: {
      color: '#FFFFFF',
      fontWeight: 'bold'
    }
  });
