import React, { useState, useRef, useEffect } from 'react';
import {
  SafeAreaView,
  StyleSheet,
  Text,
  TextInput,
  View,
  ScrollView,
  TouchableOpacity,
  KeyboardAvoidingView,
  Platform
} from 'react-native';

export default function Chat () {
  const [messages, setMessages] = useState([]);
  const [inputText, setInputText] = useState('');
  const scrollViewRef = useRef();

  // Scroll to bottom of the message list whenever a new message is added
  useEffect(() => {
    scrollViewRef.current.scrollToEnd({ animated: true });
  }, [messages]);

  // Function to handle sending a message
  const handleSendMessage = () => {
    if (inputText.trim() === '') return;
    setMessages([...messages, { text: inputText, sender: 'user' }]);
    setInputText('');
  };

  return (
    <SafeAreaView style={styles.safeAreaContainer}>
      <KeyboardAvoidingView
        behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
        style={styles.container}
      >
        <ScrollView
          ref={scrollViewRef}
          onContentSizeChange={() => scrollViewRef.current.scrollToEnd({ animated: true })}
          contentContainerStyle={styles.messageListContainer}
        >
          {messages.map((message, index) => (
            <View
              key={index}
              style={[
                styles.messageContainer,
                message.sender === 'user' ? styles.userMessageContainer : styles.otherMessageContainer
              ]}
            >
              <Text style={message.sender === 'user' ? styles.userMessageText : styles.otherMessageText}>
                {message.text}
              </Text>
            </View>
          ))}
        </ScrollView>
        <View style={styles.inputContainer}>
          <TextInput
            value={inputText}
            onChangeText={setInputText}
            placeholder="Type a message"
            style={styles.textInput}
            onSubmitEditing={handleSendMessage}
          />
          <TouchableOpacity style={styles.sendButton} onPress={handleSendMessage}>
            <Text style={styles.sendButtonText}>Send</Text>
          </TouchableOpacity>
        </View>
      </KeyboardAvoidingView>
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
    container: {
      flex: 1,
      backgroundColor: '#fff',
    },
    header: {
      backgroundColor: '#3F51B5',
      paddingTop: 40,
      paddingBottom: 10,
      paddingHorizontal: 20,
    },
    backButton: {
      color: '#fff',
      fontSize: 24,
      fontWeight: 'bold',
    },
    title: {
      color: '#fff',
      fontSize: 20,
      fontWeight: 'bold',
      marginTop: 10,
      marginBottom: 5,
    },
    chatContainer: {
      flex: 1,
      justifyContent: 'flex-end',
      paddingHorizontal: 20,
    },
    chatMessage: {
      backgroundColor: '#fff',
      alignSelf: 'flex-start',
      maxWidth: '80%',
      padding: 10,
      borderRadius: 10,
      marginBottom: 10,
    },
    chatMessageRight: {
      alignSelf: 'flex-end',
      backgroundColor: '#BDE7E2',
    },
    chatMessageText: {
      fontSize: 16,
    },
    inputContainer: {
      flexDirection: 'row',
      backgroundColor: '#ECECEC',
      padding: 5,
      borderRadius: 25,
      marginBottom: 10,
    },
    input: {
      flex: 1,
      fontSize: 16,
      paddingHorizontal: 10,
    },
    sendButton: {
      justifyContent: 'center',
      alignItems: 'center',
      backgroundColor: '#3F51B5',
      borderRadius: 25,
      width: 40,
      height: 40,
      marginLeft: 5,
    },
    sendButtonText: {
      color: '#fff',
      fontSize: 16,
    },
  });
