import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { URLaddress, loggedUser } from './App';

// Example data for chats and contacts
const exampleChats = [
  { id: 1, name: 'Alice', lastMessage: 'Hey, how are you?' },
  { id: 2, name: 'Jack', lastMessage: 'See you tomorrow!' },
  { id: 3, name: 'Lenny', lastMessage: 'Can you send me that file?' },
];

const exampleContacts = [
  { user_id: 1, first_name: 'Alice', last_name: 'Mc', email: 'alice@gmail.com' },
  { user_id: 2, first_name: 'Jack', last_name: 'Sparrow', email: 'jack@gmail.com' },
  { user_id: 3, first_name: 'Lenny', last_name: 'Stuart', email: 'lenny@gmail.com' },
];

// Define Chats component
function Chats() {
  return (
    <View style={styles.tabContent}>
      {exampleChats.map(chat => (
        <View key={chat.id} style={styles.chatItem}>
          <Text style={styles.chatName}>{chat.name}</Text>
          <Text style={styles.chatLastMessage}>{chat.lastMessage}</Text>
        </View>
      ))}
    </View>
  );
}

// Define Contacts component
function Contacts() {
  return (
    <View style={styles.tabContent}>
      {exampleContacts.map(contact => (
        <View key={contact.user_id} style={styles.contactItem}>
          <Text style={styles.contactName}>{contact.first_name +" "+ contact.last_name}</Text>
        </View>
      ))}
    </View>
  );
}

// Define Profile component
function Profile() {
  return (
    <View style={styles.tabContent}>
      <Image style={styles.profileImage} source={{ uri: 'https://www.pngitem.com/pimgs/m/78-786293_1240-x-1240-0-avatar-profile-icon-png.png' }} />
      <Text style={styles.profileName}>John Doe</Text>
      <Text style={styles.profileEmail}>john.doe@example.com</Text>
    </View>
  );
}
export default function Home() {
  
  const [activeTab, setActiveTab] = useState('Chats');

  return (
    <View style={styles.container}>
      {/* Nav bar */}
      <View style={styles.navBar}>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Chats' && styles.activeTabButton]}
          onPress={() => setActiveTab('Chats')}
        >
          <Text style={[styles.tabButtonText, activeTab === 'Chats' && styles.activeTabButtonText]}>Chats</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Contacts' && styles.activeTabButton]}
          onPress={() => setActiveTab('Contacts')}
        >
          <Text style={[styles.tabButtonText, activeTab === 'Contacts' && styles.activeTabButtonText]}>Contacts</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Profile' && styles.activeTabButton]}
          onPress={() => setActiveTab('Profile')}
        >
          <Text style={[styles.tabButtonText, activeTab === 'Profile' && styles.activeTabButtonText]}>Profile</Text>
        </TouchableOpacity>
      </View>
      
      {/* Tab content */}
      {activeTab === 'Chats' ? <Chats /> : activeTab === 'Contacts' ? <Contacts /> : <Profile />}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  navBar: {
    flexDirection: 'row',
    backgroundColor: '#007aff',
    height: 60,
  },
  tabButton: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  tabButtonText: {
    color: '#fff',
    fontSize: 18,
  },
  activeTabButton: {
    borderBottomWidth: 2,
    borderBottomColor: '#fff',
  },
  activeTabButtonText: {
    fontWeight: 'bold',
  },
  tabContent: {
    flex: 1,
    alignItems: 'centre',
    justifyContent: 'flex-start',
  }, 
  profileImage: {
    width: 100,
    height: 100,
    borderRadius: 50,
    marginBottom: 20,
  },
  profileName: {
    fontWeight: 'bold',
    fontSize: 18,
    marginBottom: 10,
  },
  profileEmail: {
    color: '#777',
    fontSize: 14,
  },
    chatItem: {
      width: '100%',
      padding: 20,
      backgroundColor: '#f0f0f0',
      paddingTop: 20,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    chatName: {
      fontWeight: 'bold',
      fontSize: 16,
      marginBottom: 5,
    },
    chatLastMessage: {
      color: '#777',
      fontSize: 14,
    },
    contactItem: {
      width: '100%',
      padding: 20,
      backgroundColor: '#f0f0f0',
      paddingTop: 20,
      borderBottomWidth: 1,
      borderBottomColor: '#ccc',
    },
    contactName: {
      fontWeight: 'bold',
      fontSize: 16,
    },
    
});
