import React, { useState } from 'react';
import { View, Text, StyleSheet, TouchableOpacity } from 'react-native';

// Example data for chats and contacts
const exampleChats = [
  { id: 1, name: 'John', lastMessage: 'Hey, how are you?' },
  { id: 2, name: 'Mary', lastMessage: 'See you tomorrow!' },
  { id: 3, name: 'Bob', lastMessage: 'Can you send me that file?' },
];

const exampleContacts = [
  { id: 1, name: 'Alice' },
  { id: 2, name: 'Jack' },
  { id: 3, name: 'Lisa' },
];

// Define Chats component
function Chats() {
  return (
    <View style={styles.tabContent}>
      {exampleChats.map(chat => (
        <View key={chat.id} style={styles.chatItem}>
          <Text style={styles.chatName}>{chat.name}</Text>
          <Text style={styles.chatLastMessage}>{chat.lastMessage}</Text>
        </View>
      ))}
    </View>
  );
}

// Define Contacts component
function Contacts() {
  return (
    <View style={styles.tabContent}>
      {exampleContacts.map(contact => (
        <View key={contact.id} style={styles.contactItem}>
          <Text style={styles.contactName}>{contact.name}</Text>
        </View>
      ))}
    </View>
  );
}

export default function Home() {
  const [activeTab, setActiveTab] = useState('Chats');

  return (
    <View style={styles.container}>
      {/* Nav bar */}
      <View style={styles.navBar}>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Chats' && styles.activeTabButton]}
          onPress={() => setActiveTab('Chats')}
        >
          <Text style={[styles.tabButtonText, activeTab === 'Chats' && styles.activeTabButtonText]}>Chats</Text>
        </TouchableOpacity>
        <TouchableOpacity
          style={[styles.tabButton, activeTab === 'Contacts' && styles.activeTabButton]}
          onPress={() => setActiveTab('Contacts')}
        >
          <Text style={[styles.tabButtonText, activeTab === 'Contacts' && styles.activeTabButtonText]}>Contacts</Text>
        </TouchableOpacity>
      </View>
      
      {/* Tab content */}
      {activeTab === 'Chats' ? <Chats /> : <Contacts />}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
  },
  navBar: {
    flexDirection: 'row',
    backgroundColor: '#007aff',
    height: 60,
  },
  tabButton: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  tabButtonText: {
    color: '#fff',
    fontSize: 18,
  },
  activeTabButton: {
    borderBottomWidth: 2,
    borderBottomColor: '#fff',
  },
  activeTabButtonText: {
    fontWeight: 'bold',
  },
  tabContent: {
    flex: 1,
    alignItems: 'flex-start',
    justifyContent: 'center',
  },
  chatItem: {
    padding: 20,
    borderBottomWidth: 1,
    borderBottomColor: '#ccc',
  },
  
    tabContainer: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      alignItems: 'center',
      backgroundColor: '#007aff',
      width: '100%',
      height: 50,
      paddingHorizontal: 20,
    },
    chatsContainer: {
      flex: 1,
      height: '100%',
      alignItems: 'flex-start',
      justifyContent: 'flex-start',
      paddingTop: 20,
      borderWidth: 7,
      borderColor: '#ccc',
    },
    chatItem: {
      width: '100%',
      padding: 10,
      backgroundColor: '#f0f0f0',
      marginTop: 10,
      borderRadius: 10,
      paddingTop: 20,
      borderWidth: 7,
      borderColor: '#ccc',
    },
    chatTitle: {
      fontWeight: 'bold',
      fontSize: 16,
      marginBottom: 5,
    },
    chatMessage: {
      color: '#777',
      fontSize: 14,
    },
    contactsContainer: {
      flex: 1,
      width: '100%',
      paddingTop: 10,
      paddingHorizontal: 20,
    },
    contactItem: {
      padding: 10,
      backgroundColor: '#f0f0f0',
      marginBottom: 10,
      borderRadius: 10,
    },
    contactName: {
      fontWeight: 'bold',
      fontSize: 16,
    },
    contactStatus: {
      color: '#777',
      fontSize: 14,
    },
    
});
